mapper.select:
/* ReportMapper.selectReportTempleteList */
SELECT ROW_NUMBER() OVER (ORDER BY REG_DATE DESC) AS RNUM ,RT_ID ,REPORT_TYPE ,REPORT_SUB_TYPE ,TEMPLATE_NAME ,FILE_SERVER_PATH ,FILE_NAME ,MIMETYPE ,USE_FLAG ,UPDATE_ID ,UPDATE_DATE ,REG_ID ,REG_DATE ,SITE_ID ,(SELECT SITE_NAME FROM TBL_SITE WHERE SITE_ID = TRT.SITE_ID) SITE_NAME ,(SELECT SG_NAME FROM TBL_SITE_GROUP WHERE SG_ID = (SELECT SG_ID FROM TBL_SITE WHERE SITE_ID = TRT.SITE_ID)) SG_NAME FROM TBL_REPORT_TEMPLATE TRT
AND USE_FLAG = 'Y'
AND SITE_ID IN (SELECT SITE_ID FROM TBL_SITE WHERE SG_ID = (SELECT SG_ID FROM TBL_SITE_GROUP WHERE SG_CD = #{searchGroup}))
AND SITE_ID = #{searchSite}
AND REPORT_TYPE = #{searchType}
AND TEMPLATE_NAME LIKE CONCAT('%', #{searchKeyword}, '%')
AND REPORT_SUB_TYPE = #{reportSubType}
ORDER BY REG_DATE DESC

SELECT RT_ID ,REPORT_TYPE ,REPORT_SUB_TYPE ,TEMPLATE_NAME ,FILE_SERVER_PATH ,FILE_NAME ,MIMETYPE ,USE_FLAG ,UPDATE_ID ,UPDATE_DATE ,REG_ID ,REG_DATE ,SITE_ID FROM TBL_REPORT_TEMPLATE TRT WHERE REPORT_TYPE = #{reportType}
AND SITE_ID = #{siteId}
AND REPORT_SUB_TYPE = '03'
AND REPORT_SUB_TYPE = #{reportSubType}
AND USE_FLAG = 'Y'

mapper.insert:
/* ReportMapper.insertReportTemplete */
INSERT INTO TBL_REPORT_TEMPLATE (
		REPORT_TYPE

		,REPORT_SUB_TYPE

		,TEMPLATE_NAME
		,FILE_SERVER_PATH
		,FILE_NAME
		,MIMETYPE
		,USE_FLAG
		,UPDATE_ID
		,UPDATE_DATE
		,REG_ID
		,REG_DATE

		,SITE_ID				 

 	) VALUES (
		#{reportType}

		,#{reportSubType}

		,#{templateName}
		,#{fileServerPath}
		,#{fileName}
		,#{mimetype}
		,#{useFlag}
		,#{updateId}
		,NOW()
		,#{regId}
		,NOW()

		,#{siteId}

 	)

mapper.update:
/* ReportMapper.updateReportTemplete */
UPDATE	TBL_REPORT_TEMPLATE
   SET	
   		REPORT_TYPE = #{reportType}
   		,TEMPLATE_NAME = #{templateName}

   		,FILE_SERVER_PATH = #{fileServerPath}
   		,FILE_NAME = #{fileName}

   		,UPDATE_ID = #{updateId}
   		,UPDATE_DATE = NOW()
   		,SITE_ID = #{siteId}
 WHERE	RT_ID = #{rtId}

/* ReportMapper.deleteReportTemplete */
UPDATE	TBL_REPORT_TEMPLATE
   SET	USE_FLAG = 'N'	
 WHERE	RT_ID = #{rtId}

